import React, {useContext, useEffect, useState}  from 'react';
import modules from "./HeaderBasketButton.module.css";
import BasketIcon from './BasketIcon';
import CartContext from '../../store/cart-context';

const HeaderBasketButton = (props) => {

 const [btnIsHighlighted, setBtnIsHighlighted] = useState(false); 
 const cartCtx=  useContext(CartContext);

 const {items} = cartCtx;

 const numberOfCartItems = items.reduce((curNumber, item) => {
   return curNumber + item.amount
 }, 0);

 

  const btnClasses = `${modules.button} ${btnIsHighlighted ? modules.bump : "" }`

  useEffect(()=> {
    if(items.length === 0){
      return;
    }
    setBtnIsHighlighted(true);

   const timer = setTimeout(() =>{
     setBtnIsHighlighted(false);   
    }, 300);

    return () => {
      clearTimeout(timer)
    };

  }, [items]);


  return (
  <button className={btnClasses} onClick= {props.onClick}>
   <span className={modules.icon}><BasketIcon/></span>   
   <span>Your Cart </span>  
   <span className={modules.badge}>{numberOfCartItems}</span>    
  </button>

  )};

export default HeaderBasketButton;
