import React, {useContext} from 'react'
import modules from "./Basket.module.css";
import Modal from '../UI/Modal';
import BasketContext from '../../store/basket-context';
import BasketElement from './BasketElement';


const Basket = (props) => {
  const basketCtx = useContext(BasketContext);

  const totalAmount = `$${basketCtx.totalAmount.toFixed(2)}`;
  const hasItems = basketCtx.elements.length > 0;

  const basketElementRemoveHandler = (id) => {
    basketCtx.removeElement(id);

  };

  const basketElementAddHandler = (element) => {
    basketCtx.addElement({...element, amount:1});
  };

    const basketElements = 
      <ul className={classes["basket-elements"]}>
        {basketCtx.elements.map((element)=> 
       (<BasketElement
          key={element.id}
          name= {element.name}
          amount= {element.amount}
          price={element.price}
          onRemove = {basketElementRemoveHandler.bind(null, element.id)}
          onAdd = {basketElementAddHandler.bind(null, element)}
          />))}
       </ul>  ; 
    


  return (
    <Modal onHideHandler={props.onHideHandler}>
        {basketElements}

        <div className={modules.total}>
         <span>Total amount</span>   
         <span>{totalAmount}</span>  
         </div>

        <div className={modules.actions}> 
        <button className={modules["button--alt"]} onClick={props.onHideHandler}>Close</button>
        {hasItems && <button className={modules.button}>Order</button>}
        </div>
    </Modal>
  )
}

export default Basket;
