import React, {useEffect, useState } from "react";
import modules from "./SuppliedDesserts.module.css";
import Card from '../UI/Card';
import DessertElement  from './DessertElement/DessertElement';


/* const DUMMY_DESSERTS = [
    {
      id: 'd1',
      name: 'Baklava',
      description: 'Turkish sweet',
      price: 22.99,
    },
    {
      id: 'd2',
      name: 'croissant',
      description: 'A french delicious sweet !',
      price: 16.5,
    },
    {
      id: 'd3',
      name: 'Turkish delight',
      description: 'A unique flavor',
      price: 12.99,
    },
    {
      id: 'd4',
      name: 'milk pudding',
      description: 'Healthy...and milky...',
      price: 18.99,
    },
  ]; */
const SuppliedDesserts = () => {

  const [desserts, setDesserts] = useState([]);

   useEffect(() => {

     const fetchDesserts = async () => {
    const response =  await fetch("https://dessert-app-6b663-default-rtdb.europe-west1.firebasedatabase.app/desserts.json")
    const responseData = await response.json();   

    const loadedDesserts = [];
    
    for (let key in responseData){
      loadedDesserts.push({
        id: key,
        name : responseData[key].name,
        description : responseData[key].description,
        price : responseData[key].price

      })
    }


  };

     fetchDesserts();
     
   }, [] )
    const mealList = DUMMY_DESSERTS.map( sweet =>{ 
      return <DessertElement 
      key = {sweet.id}
      id= {sweet.id}
      name={sweet.name} 
      description={sweet.description}
      price ={sweet.price}
      />})
      
  return (
    <section className={modules.desserts}>
        <Card>
        <ul>{mealList}</ul>
        </Card>
    </section>


  )
};

export default SuppliedDesserts;
