import React, {useContext, useState} from 'react'
import modules from "./Basket.module.css";
import Modal from '../UI/Modal';
import BasketContext from '../../store/basket-context';
import BasketElement from './BasketElement';
import Checkout from './Checkout';


const Basket = (props) => {
  const [isCheckout, setIsCheckout] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const basketCtx = useContext(BasketContext);

  const totalAmount = `$${basketCtx.totalAmount}`;
  const hasItems = basketCtx.elements.length > 0;

  const basketElementRemoveHandler = (id) => {
    basketCtx.removeElement(id);

  };

  const basketElementAddHandler = (element) => {
    basketCtx.addElement({...element, amount:1});
  };

  const orderHandler = () => {
    setIsCheckout(true);
  };

  const submitOrderHandler = async (userData) => {
    setIsSubmitting(true);

   const response =  await fetch("https://dessert-app-6b663-default-rtdb.europe-west1.firebasedatabase.app/orders.json",
    {
      method: "POST",
      body: JSON.stringify({
        user: userData,
        orderedItems: basketCtx.elements
      })
    });

  };

    const basketElements = 
      <ul className={modules["basket-elements"]}>
        {basketCtx.elements.map((element)=> 
       (<BasketElement
          key={element.id}
          name= {element.name}
          amount= {element.amount}
          price={element.price}
          onRemove = {basketElementRemoveHandler.bind(null, element.id)}
          onAdd = {basketElementAddHandler.bind(null, element)}
          />))}
       </ul>  ; 
    


  return (
    <Modal onHideHandler={props.onHideHandler}>
         
         {basketElements}

        <div className={modules.total}>
         <span>Total amount</span>   
         <span>{totalAmount}</span>  
         </div>
        {isCheckout && <Checkout onConfirm = {submitOrderHandler} onCancel= {props.onHideHandler} />}
        {!isCheckout && <div className={modules.actions}> 
        <button className={modules["button--alt"]} onClick={props.onHideHandler}>Close</button>
        {hasItems && <button className={modules.button} onClick={orderHandler}>Order</button>}
        </div> }
        
    </Modal>
  )
}

export default Basket;
